import { createStore } from 'vuex'
import Api from '@/services/Api'

export default createStore({
  state: {
    clients: [],
    parcels: []
  },
  getters: {
    getStoredClients: (state) => {
      return state.clients
  },
  getStoredParcels: (state) => {
    return state.parcels
},
  },
  mutations: {
    SET_CLIENTS(state, clients) {
      state.clients = clients
  },
  SET_PARCELS(state, parcels) {
    state.parcels = parcels
},
  },
  actions: {
    async createClient({ commit }, { payload, cb }) {
      return await Api()
          .post('clients/create/', payload)
          .then((response) => {
              if (cb) {
                  cb(response.data)
              }
              return response.data
          })
          .catch((error) => {
              return Promise.reject(error)
          })
  },
  async getAllClients({ commit, state }, { setResult=true, cb }) {
    return await Api()
        .get('/clients/list')
        .then((response) => {
            if (setResult) {
                commit('SET_CLIENTS', response.data.results)
            }
            if (cb) {
                cb(response.data.results)
            }
            return response.data.results
        })
        .catch((error) => {
            return Promise.reject(error)
        })
},
async updateClient({ commit }, { uuid, payload, cb }) {
  return await Api()
      .put(`clients/update/${uuid}/`, payload)
      .then((response) => {
          if (cb) {
              cb(response.data)
          }
          return response.data
      })
      .catch((error) => {
          return Promise.reject(error)
      })
},
async deleteClient({ commit }, { uuid, cb }) {
  return await Api()
      .delete(`clients/delete/${uuid}/`)
      .then((response) => {
          if (cb) {
              cb(response.data)
          }
          return response.data
      })
      .catch((error) => {
          return Promise.reject(error)
      })
},

async createParcel({ commit }, { payload, cb }) {
  return await Api()
      .post('parcels/create/', payload)
      .then((response) => {
          if (cb) {
              cb(response.data)
          }
          return response.data
      })
      .catch((error) => {
          return Promise.reject(error)
      })
},
async getAllParcels({ commit, state }, { setResult=true, cb }) {
return await Api()
    .get('/parcels/list')
    .then((response) => {
        if (setResult) {
            commit('SET_PARCELS', response.data.results)
        }
        if (cb) {
            cb(response.data.results)
        }
        return response.data.results
    })
    .catch((error) => {
        return Promise.reject(error)
    })
},
async updateParcel({ commit }, { uuid, payload, cb }) {
return await Api()
  .put(`parcels/update/${uuid}/`, payload)
  .then((response) => {
      if (cb) {
          cb(response.data)
      }
      return response.data
  })
  .catch((error) => {
      return Promise.reject(error)
  })
},
async deleteParcel({ commit }, { uuid, cb }) {
return await Api()
  .delete(`parcels/delete/${uuid}/`)
  .then((response) => {
      if (cb) {
          cb(response.data)
      }
      return response.data
  })
  .catch((error) => {
      return Promise.reject(error)
  })
},

